
PACKET_LEN = 4
MESSAGE_LEN = 14

def startOfPacketMarker(stream: str, packet_len:int) -> int:
    char_count = 0
    packet = []
    for char in stream:
        packet.append(char)
        char_count += 1

        while len(packet) != len(set(packet)):
            packet.pop(0)
        
        if len(packet) == packet_len:
            return char_count


print(startOfPacketMarker("", PACKET_LEN))

print(startOfPacketMarker("", MESSAGE_LEN))
